using UnityEngine; using UnityEngine.UI; public class TwinImagePopup : MonoBehaviour { public Image leftImage; public Image rightImage; public Button closeButton; void Start() { if(closeButton) closeButton.onClick.AddListener(() => Destroy(gameObject)); } public void SetImages(Sprite img1, Sprite img2) { if(leftImage) leftImage.sprite = img1; if(rightImage) rightImage.sprite = img2; } }